            for (int i = 0; i < btn.Length; i++)
            {
                btn[i] = new Button();
                btn[i].Text = i.ToString();
                btn[i].Font = new Font("Tahoma", 1);
                btn[i].BackColor = Color.White;
                btn[i].Size = new Size(btnWidth, btnHeight);
                btn[i].Location = new Point(margin + i % 20 * btn[i].Width, margin + i / 20 * btn[i].Height);
                btn[i].Click += Form1_Click;
                Controls.Add(btn[i]);
            }

            for (int i = 0; i < 10; i++)
            {
                comboBox1.Items.Add(i);
            }
        }

